import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatContainerRef = useRef(null);

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [chatHistory]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    const userMessage = message;
    setMessage('');
    setChatHistory(prev => [...prev, { type: 'user', content: userMessage }]);
    setLoading(true);

    try {
      const result = await axios.post('http://localhost:3001/api/chat', { message: userMessage });
      setChatHistory(prev => [...prev, { type: 'ai', content: result.data.reply }]);
    } catch (error) {
      console.error('Error:', error);
      setChatHistory(prev => [...prev, { type: 'ai', content: 'Sorry, I encountered an error processing your request.' }]);
    }
    setLoading(false);
  };

  const clearChat = () => {
    setChatHistory([]);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <div className="container">
      <div className="header">
        <div className="logo">
          <span className="logo-icon">ðŸ¤–</span>
          <h1 className="title">Gemini AI Chat</h1>
        </div>
        {chatHistory.length > 0 && (
          <button onClick={clearChat} className="clear-button">
            Clear Chat
          </button>
        )}
      </div>

      <div className="chat-container" ref={chatContainerRef}>
        {chatHistory.map((msg, index) => (
          <div key={index} className="message">
            <div className={`message-bubble ${msg.type}-message`}>
              {msg.content}
            </div>
          </div>
        ))}
        {loading && (
          <div className="typing-indicator">
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
            <div className="typing-dot"></div>
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className="chat-form">
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyPress={handleKeyPress}
          className="chat-input"
          placeholder="Type your message... (Press Enter to send, Shift+Enter for new line)"
        />
        <button type="submit" disabled={loading || !message.trim()} className="send-button">
          {loading ? 'Sending...' : 'Send Message'}
        </button>
      </form>
    </div>
  );
}

export default App;
